import{_ as a,f as e}from"./app.9d0d0a36.js";const h={},d=e('<p>@<a href="%E8%AE%A1%E7%AE%97%E6%9C%BA%E7%BD%91%E7%BB%9C%E6%B1%87%E6%80%BB">TOC</a></p><h1 id="\u8BA1\u7B97\u673A\u7F51\u7EDC\u6C47\u603B" tabindex="-1"><a class="header-anchor" href="#\u8BA1\u7B97\u673A\u7F51\u7EDC\u6C47\u603B" aria-hidden="true">#</a> \u8BA1\u7B97\u673A\u7F51\u7EDC\u6C47\u603B</h1><h2 id="\u5E94\u7528\u5C42-\u5E94\u7528-\u8868\u793A-\u4F1A\u8BDD" tabindex="-1"><a class="header-anchor" href="#\u5E94\u7528\u5C42-\u5E94\u7528-\u8868\u793A-\u4F1A\u8BDD" aria-hidden="true">#</a> \u5E94\u7528\u5C42(\u5E94\u7528,\u8868\u793A,\u4F1A\u8BDD)</h2><h3 id="http" tabindex="-1"><a class="header-anchor" href="#http" aria-hidden="true">#</a> http</h3><h3 id="https" tabindex="-1"><a class="header-anchor" href="#https" aria-hidden="true">#</a> https</h3><p>\u5DE5\u4F5C\u539F\u7406HTTPS\u5DE5\u4F5C\u539F\u7406</p><ul><li>\u4E00\u3001\u9996\u5148HTTP\u8BF7\u6C42\u670D\u52A1\u7AEF\u751F\u6210\u8BC1\u4E66\uFF0C\u5BA2\u6237\u7AEF\u5BF9\u8BC1\u4E66\u7684\u6709\u6548\u671F\u3001\u5408\u6CD5\u6027\u3001\u57DF\u540D\u662F\u5426\u4E0E\u8BF7\u6C42\u7684\u57DF\u540D\u4E00\u81F4\u3001\u8BC1\u4E66\u7684\u516C\u94A5\uFF08RSA\u52A0\u5BC6\uFF09\u7B49\u8FDB\u884C\u6821\u9A8C\uFF1B</li><li>\u4E8C\u3001\u5BA2\u6237\u7AEF\u5982\u679C\u6821\u9A8C\u901A\u8FC7\u540E\uFF0C\u5C31\u6839\u636E\u8BC1\u4E66\u7684\u516C\u94A5\u7684\u6709\u6548\uFF0C \u751F\u6210\u968F\u673A\u6570\uFF0C\u968F\u673A\u6570\u4F7F\u7528\u516C\u94A5\u8FDB\u884C\u52A0\u5BC6\uFF08RSA\u52A0\u5BC6\uFF09\uFF1B</li><li>\u4E09\u3001\u6D88\u606F\u4F53\u4EA7\u751F\u7684\u540E\uFF0C\u5BF9\u5B83\u7684\u6458\u8981\u8FDB\u884CMD5\uFF08\u6216\u8005SHA1\uFF09\u7B97\u6CD5\u52A0\u5BC6\uFF0C\u6B64\u65F6\u5C31\u5F97\u5230\u4E86RSA\u7B7E\u540D\uFF1B</li><li>\u56DB\u3001\u53D1\u9001\u7ED9\u670D\u52A1\u7AEF\uFF0C\u6B64\u65F6\u53EA\u6709\u670D\u52A1\u7AEF\uFF08RSA\u79C1\u94A5\uFF09\u80FD\u89E3\u5BC6\u3002</li><li>\u4E94\u3001\u89E3\u5BC6\u5F97\u5230\u7684\u968F\u673A\u6570\uFF0C\u518D\u7528AES\u52A0\u5BC6\uFF0C\u4F5C\u4E3A\u5BC6\u94A5\uFF08\u6B64\u65F6\u7684\u5BC6\u94A5\u53EA\u6709\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u77E5\u9053\uFF09\u3002</li></ul><p><img src="https://img-blog.csdnimg.cn/img_convert/d4dfb9247b64ae1933f7a568ea11af17.png#pic_center" alt="\u5728\u8FD9\u91CC\u63D2\u5165\u56FE\u7247\u63CF\u8FF0" loading="lazy"><img src="https://img-blog.csdnimg.cn/img_convert/e9a870e457dca2f63dffe8272f970ab5.png#pic_center" alt="\u5728\u8FD9\u91CC\u63D2\u5165\u56FE\u7247\u63CF\u8FF0" loading="lazy"></p><h3 id="dns" tabindex="-1"><a class="header-anchor" href="#dns" aria-hidden="true">#</a> DNS</h3><p>\u57DF\u540D\u7CFB\u7EDF\uFF08\u82F1\u6587\uFF1ADomain Name System\uFF0C\u7F29\u5199\uFF1ADNS\uFF09\u662F\u4E92\u8054\u7F51\u7684\u4E00\u9879\u670D\u52A1\u3002\u5B83\u4F5C\u4E3A\u5C06\u57DF\u540D\u548CIP\u5730\u5740\u76F8\u4E92\u6620\u5C04\u7684\u4E00\u4E2A\u5206\u5E03\u5F0F\u6570\u636E\u5E93\uFF0C\u80FD\u591F\u4F7F\u4EBA\u66F4\u65B9\u4FBF\u5730\u8BBF\u95EE\u4E92\u8054\u7F51\u3002DNS\u4F7F\u7528TCP\u548CUDP\u7AEF\u53E353\u3002\u5F53\u524D\uFF0C\u5BF9\u4E8E\u6BCF\u4E00\u7EA7\u57DF\u540D\u957F\u5EA6\u7684\u9650\u5236\u662F63\u4E2A\u5B57\u7B26\uFF0C\u57DF\u540D\u603B\u957F\u5EA6\u5219\u4E0D\u80FD\u8D85\u8FC7253\u4E2A\u5B57\u7B26\u30022.1 \u672C\u5730\u57DF\u540D\u670D\u52A1\u5668LDNS</p><p>\u6BCF\u4E2A\u7535\u8111\u91CC\u9762\u90FD\u8BBE\u7F6E\u4E86\u672C\u5730DNS\u670D\u52A1\u5668\uFF08\u7B80\u79F0LDNS\uFF09\uFF0C\u9700\u8981\u7684\u65F6\u5019\uFF0C\u5C31\u5411LDNS\u53D1\u51FA\u8BF7\u6C42\uFF0CLDNS\u5728\u7F51\u4E0A\u95EE\u6743\u5A01\u57DF\u540D\u670D\u52A1\u5668\uFF08\u7B80\u79F0\u6743\u5A01DNS\uFF09\u5F97\u5230\u7B54\u6848\u3002</p><p>\u6743\u5A01\u57DF\u540D\u670D\u52A1\u5668</p><p>\u95EE\u6211\u4E00\u4E2A\u57DF\u540D\uFF0C\u6211\u544A\u8BC9\u4F60IP\uFF0C\u5982\u679C\u6211\u4E0D\u77E5\u9053\uFF0C\u6211\u544A\u8BC9\u4F60\u8C01\u53EF\u80FD\u77E5\u9053\uFF0C\u4F60\u518D\u53BB\u95EE\u5B83\u3002 \u8D1F\u8D23\u5BF9\u8BF7\u6C42\u4F5C\u51FA\u6743\u5A01\u7684\u56DE\u7B54\u3002\u6743\u5A01DNS\u4E2D\u5B58\u50A8\u7740\u8BB0\u5F55\uFF0C\u6700\u5E38\u89C1\u76843\u79CD\uFF1AA\u8BB0\u5F55\uFF08\u8BB0\u5F55\u67D0\u57DF\u540D\u548C\u5176IP\u7684\u5BF9\u5E94\uFF09\uFF0CNS\u8BB0\u5F55\uFF08\u8BB0\u5F55\u67D0\u57DF\u540D\u548C\u8D1F\u8D23\u89E3\u6790\u8BE5\u57DF\u7684\u6743\u5A01DNS\uFF09\uFF0CCNAME\u8BB0\u5F55\uFF08\u8D1F\u8D23\u8BB0\u5F55\u67D0\u57DF\u540D\u53CA\u5176\u522B\u540D\uFF09\u3002\u6743\u5A01\u80FD\u76F4\u63A5\u56DE\u7B54\u7684\uFF0C\u5C31\u56DEA\u8BB0\u5F55\uFF1B\u9700\u8981\u5176\u4ED6\u6743\u5A01DNS\u56DE\u7B54\u7684\uFF0C\u5C31\u56DENS\u8BB0\u5F55\uFF0C\u7136\u540ELDNS\u518D\u53BB\u627E\u5176\u4ED6\u6743\u5A01DNS\u95EE\uFF1B\u5982\u679C\u8BE5\u8BB0\u5F55\u662F\u522B\u540D\u7C7B\u578B\u7684\uFF0C\u5C31\u56DECNAME\uFF0CLDNS\u5C31\u4F1A\u518D\u53BB\u89E3\u6790\u522B\u540D\u3002</p><p>\u6839\u57DF\u540D\u670D\u52A1\u5668\u2014\u6839DNS</p><p>\u5F53LDNS\u5565\u90FD\u4E0D\u77E5\u9053\u7684\u65F6\u5019\uFF08\u4E5F\u5373\u6CA1\u6709\u4EFB\u4F55\u7F13\u5B58\uFF09\uFF0C\u5C31\u53BB\u95EE\u6839DNS\uFF0C\u6839\u80FD\u544A\u8BC9LDNS\u4E0B\u4E00\u6B65\u8BE5\u95EE\u8C01\u3002</p><p>\u5177\u4F53\u8FC7\u7A0B</p><ul><li>\u6743\u5A01DNS\uFF1A\u6743\u5A01\u80FD\u76F4\u63A5\u56DE\u7B54\u7684\uFF0C\u5C31\u56DEip\uFF1B\u9700\u8981\u5176\u4ED6\u6743\u5A01DNS\u56DE\u7B54\u7684\uFF0C\u5C31\u56DENS\u8BB0\u5F55\uFF08\u8BB0\u5F55\u67D0\u57DF\u540D\u548C\u8D1F\u8D23\u89E3\u6790\u8BE5\u57DF\u7684\u6743\u5A01DNS\uFF09\uFF0C\u7136\u540ELDNS\u518D\u53BB\u627E\u5176\u4ED6\u6743\u5A01DNS\u95EE\uFF1B\u5982\u679C\u8BE5\u8BB0\u5F55\u662F\u522B\u540D\u7C7B\u578B\u7684\uFF0C\u5C31\u56DECNAME\uFF08\u8D1F\u8D23\u8BB0\u5F55\u67D0\u57DF\u540D\u53CA\u5176\u522B\u540D\uFF09\uFF0CLDNS\u5C31\u4F1A\u518D\u53BB\u89E3\u6790\u522B\u540D\u3002</li><li>\u9012\u5F52DNS\uFF1A\u901A\u5E38\u5C31\u662FLDNS\uFF0C\u5B83\u63A5\u53D7\u7EC8\u7AEF\u7684\u57DF\u540D\u67E5\u8BE2\u8BF7\u6C42\uFF0C\u8D1F\u8D23\u5728\u7F51\u4E0A\u95EE\u4E00\u5708\u540E\uFF0C\u5C06\u7B54\u6848\u8FD4\u56DE\u7EC8\u7AEF\u3002</li></ul><h2 id="\u4F20\u8F93\u5C42" tabindex="-1"><a class="header-anchor" href="#\u4F20\u8F93\u5C42" aria-hidden="true">#</a> \u4F20\u8F93\u5C42</h2><h3 id="tcp" tabindex="-1"><a class="header-anchor" href="#tcp" aria-hidden="true">#</a> TCP</h3><p>\u4E3B\u8981\u7279\u70B9\uFF081\uFF09\u6162\u542F\u52A8</p><p>\u6BCF\u5F53\u5EFA\u7ACB\u4E00\u4E2ATCP\u8FDE\u63A5\u65F6\u6216\u4E00\u4E2ATCP\u8FDE\u63A5\u53D1\u751F\u8D85\u65F6\u91CD\u4F20\u540E\uFF0C\u8BE5\u8FDE\u63A5\u4FBF\u8FDB\u5165\u6162\u542F\u52A8\u9636\u6BB5\u3002\u8FDB\u5165\u6162\u542F\u52A8\u540E\uFF0CTCP\u5B9E\u4F53\u5C06\u62E5\u585E\u7A97\u53E3\u7684\u5927\u5C0F\u521D\u59CB\u5316\u4E3A\u4E00\u4E2A\u62A5\u6587\u6BB5\uFF0C\u5373\uFF1Acwnd=1\u3002\u6B64\u540E\uFF0C\u6BCF\u6536\u5230\u4E00\u4E2A\u62A5\u6587\u6BB5\u7684\u786E\u8BA4\uFF08ACK\uFF09\uFF0Ccwnd\u503C\u52A01\uFF0C\u5373\u62E5\u585E\u7A97\u53E3\u6309\u6307\u6570\u589E\u52A0\u3002\u5F53cwnd\u503C\u8D85\u8FC7\u6162\u542F\u52A8\u9610\u503C\uFF08ssthresh\uFF09\u6216\u53D1\u751F\u62A5\u6587\u6BB5\u4E22\u5931\u91CD\u4F20\u65F6\uFF0C\u6162\u542F\u52A8\u9636\u6BB5\u7ED3\u675F\u3002\u524D\u8005\u8FDB\u5165\u62E5\u585E\u907F\u514D\u9636\u6BB5\uFF0C\u540E\u8005\u91CD\u65B0\u8FDB\u5165\u6162\u542F\u52A8\u9636\u6BB5\u3002</p><p>\uFF082\uFF09\u62E5\u585E\u907F\u514D</p><p>\u5728\u6162\u542F\u9636\u6BB5\uFF0C\u5F53cwnd\u503C\u8D85\u8FC7\u6162\u542F\u52A8\u9610\u503C\uFF08ssthresh\uFF09\u540E\uFF0C\u6162\u542F\u52A8\u8FC7\u7A0B\u7ED3\u675F\uFF0CTCP\u8FDE\u63A5\u8FDB\u5165\u62E5\u585E\u907F\u514D\u9636\u6BB5\u3002\u5728\u62E5\u585E\u907F\u514D\u9636\u6BB5\uFF0C\u6BCF\u4E00\u6B21\u53D1\u9001\u7684cwnd\u4E2A\u62A5\u6587\u6BB5\u88AB\u5B8C\u5168\u786E\u8BA4\u540E\uFF0C\u624D\u5C06cwnd\u503C\u52A01\u3002\u5728\u6B64\u9636\u6BB5\uFF0Ccwnd\u503C\u7EBF\u6027\u589E\u52A0\u3002</p><p>\uFF083\uFF09\u5FEB\u901F\u91CD\u4F20</p><p>\u5FEB\u901F\u91CD\u4F20\u662F\u5BF9\u8D85\u65F6\u91CD\u4F20\u7684\u6539\u8FDB\u3002\u5F53\u6E90\u7AEF\u6536\u5230\u5BF9\u540C\u4E00\u4E2A\u62A5\u6587\u7684\u4E09\u4E2A\u91CD\u590D\u786E\u8BA4\u65F6\uFF0C\u5C31\u786E\u5B9A\u4E00\u4E2A\u62A5\u6587\u6BB5\u5DF2\u7ECF\u4E22\u5931\uFF0C\u56E0\u6B64\u7ACB\u523B\u91CD\u4F20\u4E22\u5931\u7684\u62A5\u6587\u6BB5\uFF0C\u800C\u4E0D\u5FC5\u7B49\u5230\u91CD\u4F20\u5B9A\u65F6\u5668\uFF08RTO\uFF09\u8D85\u65F6\u3002\u4EE5\u6B64\u51CF\u5C11\u4E0D\u5FC5\u8981\u7684\u7B49\u5F85\u65F6\u95F4\u3002</p><p>\uFF084\uFF09\u5FEB\u901F\u6062\u590D</p><p>\u5FEB\u901F\u6062\u590D\u662F\u5BF9\u4E22\u5931\u6062\u590D\u673A\u5236\u7684\u6539\u8FDB\u3002\u5728\u5FEB\u901F\u91CD\u4F20\u4E4B\u540E\uFF0C\u4E0D\u7ECF\u8FC7\u6162\u542F\u52A8\u8FC7\u7A0B\u800C\u76F4\u63A5\u8FDB\u5165\u62E5\u585E\u907F\u514D\u9636\u6BB5\u3002\u6BCF\u5F53\u5FEB\u901F\u91CD\u4F20\u540E\uFF0C\u7F6Essthresh=cwnd/2\u3001ewnd=ssthresh+3\u3002\u6B64\u540E\uFF0C\u6BCF\u6536\u5230\u4E00\u4E2A\u91CD\u590D\u786E\u8BA4\uFF0C\u5C06cwnd\u503C\u52A01\uFF0C\u76F4\u81F3\u6536\u5230\u5BF9\u4E22\u5931\u62A5\u6587\u6BB5\u548C\u5176\u540E\u82E5\u5E72\u62A5\u6587\u6BB5\u7684\u7D2F\u79EF\u786E\u8BA4\u540E\uFF0C\u7F6Ecwnd=ssthresh\uFF0C\u8FDB\u5165\u62E5\u585E\u907F\u514D\u9636\u6BB5\u3002 <img src="https://img-blog.csdnimg.cn/img_convert/b46604c9f6016d320361b562581ffa4d.png" alt="img" loading="lazy"></p><h3 id="udp" tabindex="-1"><a class="header-anchor" href="#udp" aria-hidden="true">#</a> UDP</h3><p>UDP\uFF08\u7528\u6237\u6570\u636E\u62A5\u534F\u8BAE\uFF09\uFF1A\u662FOSI\u53C2\u8003\u6A21\u578B\u4E2D\u7684\u4E00\u79CD\u65E0\u8FDE\u63A5<strong>\u4F20\u8F93\u5C42</strong>\u534F\u8BAE\uFF0C\u63D0\u4F9B\u9762\u5411\u4E8B\u52A1\u7684\u7B80\u5355\u4E0D\u53EF\u9760\u4FE1\u606F\u4F20\u8F93\u670D\u52A1\u3002 \u7279\u70B9\uFF1A \uFF081\uFF09udp\u662F\u65E0\u8FDE\u63A5\u7684 \u5373\u53D1\u9001\u6570\u636E\u62A5\u65E0\u9700\u5EFA\u7ACB\u8FDE\u63A5\uFF0C\u5E76\u56E0\u6B64\u51CF\u5C11\u4E86\u5F00\u9500\u548C\u53D1\u9001\u6570\u636E\u4E4B\u524D\u4E0D\u5FC5\u8981\u7684\u65F6\u5EF6\uFF1B \uFF082\uFF09\u4F7F\u7528\u5C3D\u91CF\u5927\u52AA\u529B\u4EA4\u4ED8\uFF1B \uFF083\uFF09\u662F\u9762\u5411\u62A5\u6587\u7684</p><h2 id="\u7F51\u7EDC\u5C42" tabindex="-1"><a class="header-anchor" href="#\u7F51\u7EDC\u5C42" aria-hidden="true">#</a> \u7F51\u7EDC\u5C42</h2><h3 id="arp" tabindex="-1"><a class="header-anchor" href="#arp" aria-hidden="true">#</a> ARP</h3><p>\u6BCF\u53F0\u4E3B\u673A\u90FD\u4F1A\u5728\u81EA\u5DF1\u7684ARP\u7F13\u51B2\u533A\u4E2D\u5EFA\u7ACB\u4E00\u4E2A ARP\u5217\u8868\uFF0C\u4EE5\u8868\u793AIP\u5730\u5740\u548CMAC\u5730\u5740\u7684\u5BF9\u5E94\u5173\u7CFB\u3002</p><p>\u5F53\u6E90\u4E3B\u673A\u9700\u8981\u5C06\u4E00\u4E2A\u6570\u636E\u5305\u8981\u53D1\u9001\u5230\u76EE\u7684\u4E3B\u673A\u65F6\uFF0C\u4F1A\u9996\u5148\u68C0\u67E5\u81EA\u5DF1 ARP\u5217\u8868\u4E2D\u662F\u5426\u5B58\u5728\u8BE5 IP\u5730\u5740\u5BF9\u5E94\u7684MAC\u5730\u5740\u3002</p><p>\u5982\u679C\u6709\uFF0C\u5C31\u76F4\u63A5\u5C06\u6570\u636E\u5305\u53D1\u9001\u5230\u8FD9\u4E2AMAC\u5730\u5740\uFF1B\u5982\u679C\u6CA1\u6709\uFF0C\u5C31\u5411\u672C\u5730\u7F51\u6BB5\u53D1\u8D77\u4E00\u4E2AARP\u8BF7\u6C42\u7684\u5E7F\u64AD\u5305\uFF0C\u67E5\u8BE2\u6B64\u76EE\u7684\u4E3B\u673A\u5BF9\u5E94\u7684MAC\u5730\u5740\u3002</p><p>\u6B64ARP\u8BF7\u6C42\u6570\u636E\u5305\u91CC\u5305\u62EC\u6E90\u4E3B\u673A\u7684IP\u5730\u5740\u3001\u786C\u4EF6\u5730\u5740\u3001\u4EE5\u53CA\u76EE\u7684\u4E3B\u673A\u7684IP\u5730\u5740\u3002\u7F51\u7EDC\u4E2D\u6240\u6709\u7684\u4E3B\u673A\u6536\u5230\u8FD9\u4E2AARP\u8BF7\u6C42\u540E\uFF0C\u4F1A\u68C0\u67E5\u6570\u636E\u5305\u4E2D\u7684\u76EE\u7684IP\u662F\u5426\u548C\u81EA\u5DF1\u7684IP\u5730\u5740\u4E00\u81F4\u3002</p><p>\u5982\u679C\u4E0D\u76F8\u540C\u5C31\u5FFD\u7565\u6B64\u6570\u636E\u5305\uFF1B\u5982\u679C\u76F8\u540C\uFF0C\u8BE5\u4E3B\u673A\u9996\u5148\u5C06\u53D1\u9001\u7AEF\u7684MAC\u5730\u5740\u548CIP\u5730\u5740\u6DFB\u52A0\u5230\u81EA\u5DF1\u7684ARP\u5217\u8868\u4E2D\u3002</p><p>\u5982\u679CARP\u8868\u4E2D\u5DF2\u7ECF\u5B58\u5728\u8BE5IP\u7684\u4FE1\u606F\uFF0C\u5219\u5C06\u5176\u8986\u76D6\uFF0C\u7136\u540E\u7ED9\u6E90\u4E3B\u673A\u53D1\u9001\u4E00\u4E2A ARP\u54CD\u5E94\u6570\u636E\u5305\uFF0C\u544A\u8BC9\u5BF9\u65B9\u81EA\u5DF1\u662F\u5B83\u9700\u8981\u67E5\u627E\u7684MAC\u5730\u5740\u3002</p><p>\u6E90\u4E3B\u673A\u6536\u5230\u8FD9\u4E2AARP\u54CD\u5E94\u6570\u636E\u5305\u540E\uFF0C\u5C06\u5F97\u5230\u7684\u76EE\u7684\u4E3B\u673A\u7684IP\u5730\u5740\u548CMAC\u5730\u5740\u6DFB\u52A0\u5230\u81EA\u5DF1\u7684ARP\u5217\u8868\u4E2D\uFF0C\u5E76\u5229\u7528\u6B64\u4FE1\u606F\u5F00\u59CB\u6570\u636E\u7684\u4F20\u8F93\u3002</p><p>\u5982\u679C\u6E90\u4E3B\u673A\u4E00\u76F4\u6CA1\u6709\u6536\u5230ARP\u54CD\u5E94\u6570\u636E\u5305\uFF0C\u8868\u793AARP\u67E5\u8BE2\u5931\u8D25\u3002</p><h3 id="rarp" tabindex="-1"><a class="header-anchor" href="#rarp" aria-hidden="true">#</a> RARP</h3><p>ARP\u534F\u8BAE\u662F\u6839\u636EIP\u5730\u5740\u627E\u5176\u5BF9\u5E94\u7684MAC\u5730\u5740\uFF0C\u800CRARP\u5219\u662F\u6839\u636EMAC\u5730\u5740\u627E\u5176\u5BF9\u5E94IP\u5730\u5740\uFF0C\u6240\u4EE5\u79F0\u4E4B\u4E3A&quot;\u53CD\u5411ARP&quot;\u3002\u5177\u6709\u672C\u5730\u78C1\u76D8\u7684\u7CFB\u7EDF\u5F15\u5BFC\u65F6\uFF0C\u4E00\u822C\u662F\u4ECE\u78C1\u76D8\u4E0A\u7684\u914D\u7F6E\u6587\u4EF6\u4E2D\u8BFB\u53D6IP\u5730\u5740\uFF0C\u7136\u540E\u5373\u53EF\u76F4\u63A5\u7528ARP\u534F\u8BAE\u627E\u51FA\u4E0E\u5176\u5BF9\u5E94\u7684\u4E3B\u673AMAC\u5730\u5740\u3002\u4F46\u662F\u65E0\u76D8\u673A\uFF0C\u5982X\u7EC8\u7AEF\u6216\u65E0\u76D8\u5DE5\u4F5C\u7AD9\uFF0C\u542F\u52A8\u65F6\u662F\u901A\u8FC7MAC\u5730\u5740\u6765\u5BFB\u5740\u7684\uFF0C\u8FD9\u65F6\u5C31\u9700\u8981\u901A\u8FC7RARP\u534F\u8BAE\u83B7\u53D6IP\u5730\u5740\u3002</p><p>RARP\u7684\u57FA\u672C\u5DE5\u4F5C\u539F\u7406\u5982\u4E0B\uFF1A</p><p>\uFF081\uFF09\u53D1\u9001\u7AEF\u53D1\u9001\u4E00\u4E2A\u672C\u5730\u7684RARP\u5E7F\u64AD\u5305\uFF0C\u5728\u6B64\u5E7F\u64AD\u5305\u4E2D\u58F0\u660E\u81EA\u5DF1\u7684MAC\u5730\u5740\uFF0C\u5E76\u4E14\u8BF7\u6C42\u4EFB\u4F55\u6536\u5230\u6B64\u8BF7\u6C42\u7684RARP\u670D\u52A1\u5668\u5206\u914D\u4E00\u4E2AIP\u5730\u5740\u3002</p><p>\uFF082\uFF09\u672C\u5730\u7F51\u6BB5\u4E0A\u7684RARP\u670D\u52A1\u5668\u6536\u5230\u6B64\u8BF7\u6C42\u540E\uFF0C\u68C0\u67E5\u5176RARP\u5217\u8868\uFF0C\u67E5\u627E\u8BE5MAC\u5730\u5740\u5BF9\u5E94\u7684IP\u5730\u5740\u3002\u5982\u679C\u5B58\u5728\uFF0CRARP\u670D\u52A1\u5668\u5C31\u7ED9\u6E90\u4E3B\u673A\u53D1\u9001\u4E00\u4E2A\u54CD\u5E94\u6570\u636E\u5305\uFF0C\u5E76\u5C06\u6B64IP\u5730\u5740\u63D0\u4F9B\u7ED9\u5BF9\u65B9\u4E3B\u673A\u4F7F\u7528\uFF1B\u5982\u679C\u4E0D\u5B58\u5728\uFF0CRARP\u670D\u52A1\u5668\u5BF9\u6B64\u4E0D\u505A\u4EFB\u4F55\u54CD\u5E94\u3002</p><p>\uFF083\uFF09\u6E90\u7AEF\u5728\u6536\u5230\u4ECERARP\u670D\u52A1\u5668\u6765\u7684\u54CD\u5E94\u4FE1\u606F\u540E\uFF0C\u5229\u7528\u5F97\u5230\u7684IP\u5730\u5740\u8FDB\u884C\u901A\u4FE1\uFF1B\u5982\u679C\u4E00\u76F4\u6CA1\u6709\u6536\u5230RARP\u670D\u52A1\u5668\u7684\u54CD\u5E94\u4FE1\u606F\uFF0C\u5219\u8868\u793A\u521D\u59CB\u5316\u5931\u8D25\u3002</p><h2 id="\u94FE\u8DEF\u5C42" tabindex="-1"><a class="header-anchor" href="#\u94FE\u8DEF\u5C42" aria-hidden="true">#</a> \u94FE\u8DEF\u5C42</h2><h3 id="\u4EE5\u592A\u7F51\u534F\u8BAE" tabindex="-1"><a class="header-anchor" href="#\u4EE5\u592A\u7F51\u534F\u8BAE" aria-hidden="true">#</a> \u4EE5\u592A\u7F51\u534F\u8BAE</h3><p>\u4EE5\u592A\u7F51\u89C4\u5B9A\uFF0C\u4E00\u7EC4\u7535\u4FE1\u53F7\u6784\u6210\u4E00\u4E2A\u6570\u636E\u5305\uFF0C\u53EB\u505A\u5E27\uFF0C\u6BCF\u4E00\u5E27\u5206\u6210\u4E24\u4E2A\u90E8\u5206\uFF1A\u6807\u5934\uFF08Head\uFF09\u548C\u6570\u636E\uFF08Data\uFF09\u3002</p><p>\u201C\u6807\u5934\u201D\u5305\u542B\u6570\u636E\u5305\u7684\u4E00\u4E9B\u8BF4\u660E\u9879\uFF0C\u6BD4\u5982\u53D1\u9001\u8005\u3001\u63A5\u53D7\u8005\u3001\u6570\u636E\u7C7B\u578B\u7B49\u7B49\uFF1B\u201C\u6570\u636E\u201D\u5219\u662F\u6570\u636E\u5305\u7684\u5177\u4F53\u5185\u5BB9\u3002</p><p>\u201C\u6807\u5934\u201D\u7684\u957F\u5EA6\uFF0C\u56FA\u5B9A\u4E3A18\u5B57\u8282\u3002\u201C\u6570\u636E\u201D\u7684\u957F\u5EA6\uFF0C\u6700\u77ED\u4E3A46\u5B57\u8282\uFF0C\u6700\u957F\u4E3A1500\u5B57\u8282\u3002\u56E0\u6B64\uFF0C\u6574\u4E2A\u5E27\u6700\u77ED\u4E3A64\u5B57\u8282\uFF0C\u6700\u957F\u4E3A1518\u5B57\u8282\u3002\u5982\u679C\u6570\u636E\u5F88\u957F\uFF0C\u5C31\u5FC5\u987B\u5206\u5272\u6210\u591A\u4E2A\u5E27\u8FDB\u884C\u53D1\u9001\u3002</p><h3 id="mac\u5730\u5740" tabindex="-1"><a class="header-anchor" href="#mac\u5730\u5740" aria-hidden="true">#</a> MAC\u5730\u5740</h3><p>\u53D1\u9001\u8005\u548C\u63A5\u53D7\u8005\u5982\u4F55\u6807\u8BC6\u5462\uFF1F</p><p>\u4EE5\u592A\u7F51\u89C4\u5B9A\uFF0C\u8FDB\u5165\u7F51\u7EDC\u6240\u6709\u8BBE\u5907\uFF0C\u90FD\u5FC5\u987B\u5177\u6709\u201C\u7F51\u5361\u201D\u63A5\u53E3\u3002\u6570\u636E\u5305\u5FC5\u987B\u662F\u4ECE\u4E00\u5757\u7F51\u5361\u3002\u4F20\u9001\u5230\u53E6\u4E00\u5757\u7F51\u5361\u3002\u7F51\u5361\u7684\u5730\u5740\uFF0C\u5C31\u662F\u6570\u636E\u5305\u7684\u53D1\u9001\u5730\u5740\u548C\u63A5\u6536\u5730\u5740\uFF0C\u8FD9\u53EB\u505AMAC\u5730\u5740\u3002</p><p>\u6BCF\u5757\u7F51\u5361\u51FA\u5382\u7684\u65F6\u5019\uFF0C\u90FD\u6709\u4E00\u4E2A\u5168\u4E16\u754C\u72EC\u4E00\u65E0\u4E8C\u7684\uFF2D\uFF21\uFF23\u5730\u5740\uFF0C\u957F\u5EA6\u662F\uFF14\uFF18\u4E2A\u4E8C\u8FDB\u5236\u4F4D\uFF0C\u901A\u5E38\u7528\uFF11\uFF12\u4E2A\u5341\u516D\u8FDB\u5236\u6570\u8868\u793A\u3002</p><h2 id="\u7269\u7406\u5C42" tabindex="-1"><a class="header-anchor" href="#\u7269\u7406\u5C42" aria-hidden="true">#</a> \u7269\u7406\u5C42</h2>',55);function r(p,i){return d}var t=a(h,[["render",r]]);export{t as default};
